import React, { useState } from 'react';

const FormValidation = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    acceptTerms: false
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showTermsModal, setShowTermsModal] = useState(false);

  const validateForm = () => {
    const newErrors = {};

    // Username validation
    if (!formData.username.trim()) {
      newErrors.username = 'Username is required';
    } else if (formData.username.length < 3) {
      newErrors.username = 'Username must be at least 3 characters';
    }

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      newErrors.email = 'Please enter a valid email';
    }

    // Password validation
    const passwordError = validatePassword(formData.password);
    if (passwordError) {
      newErrors.password = passwordError;
    }

    // Confirm password validation
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ''
      });
    }
  };

  const toggleTermsModal = () => {
    setShowTermsModal(!showTermsModal);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      setIsSubmitting(true);
      // Simulate form submission
      console.log('Form submitted:', formData);
      setTimeout(() => {
        alert('Form submitted successfully!');
        setIsSubmitting(false);
      }, 1000);
    }
  };

  // Check password requirements and return error message if any
  const validatePassword = (password) => {
    if (!password) return 'Password is required';
    if (password.length < 8) return 'Password must be at least 8 characters';
    if (!/[a-z]/.test(password)) return 'Password must contain at least one lowercase letter';
    if (!/[A-Z]/.test(password)) return 'Password must contain at least one uppercase letter';
    if (!/\d/.test(password)) return 'Password must contain at least one number';
    if (!/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>/?]/.test(password)) {
      return 'Password must contain at least one special character';
    }
    return '';
  };

  // Get password error message if any
  const getPasswordError = () => {
    if (!formData.password) return 'Password is required';
    return validatePassword(formData.password);
  };

  // Only show error after user has started typing in the password field
  const showPasswordError = formData.password !== '';

  return (
    <div className="form-container">
      <h2>Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Username</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            className={errors.username ? 'error' : ''}
          />
          {errors.username && <span className="error-message">{errors.username}</span>}
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={errors.email ? 'error' : ''}
          />
          {errors.email && <span className="error-message">{errors.email}</span>}
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className={errors.password ? 'error' : ''}
            placeholder="Enter your password"
          />
          {showPasswordError && errors.password && (
            <span className="error-message">{errors.password}</span>
          )}
        </div>

        <div className="form-group">
          <label>Confirm Password</label>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            className={errors.confirmPassword ? 'error' : ''}
          />
          {errors.confirmPassword && (
            <span className="error-message">{errors.confirmPassword}</span>
          )}
        </div>

        <div className="terms-group">
          <label className="checkbox-container">
            <input
              type="checkbox"
              name="acceptTerms"
              checked={formData.acceptTerms}
              onChange={handleChange}
            />
            <span className="checkmark"></span>
            I agree to the 
            <button type="button" onClick={toggleTermsModal} className="terms-link">
              Terms and Conditions
            </button>
          </label>
          {errors.acceptTerms && (
            <span className="error-message">{errors.acceptTerms}</span>
          )}
        </div>

        <button 
          type="submit" 
          disabled={isSubmitting || !formData.acceptTerms}
          className={!formData.acceptTerms ? 'disabled' : ''}
        >
          {isSubmitting ? 'Submitting...' : 'Submit'}
        </button>

        {showTermsModal && (
          <div className="modal-overlay">
            <div className="modal-content">
              <h3>Terms and Conditions</h3>
              <div className="modal-body">
                <p>By using our service, you agree to the following terms and conditions:</p>
                <ul>
                  <li>You must be at least 18 years old to use this service.</li>
                  <li>You are responsible for maintaining the confidentiality of your account.</li>
                  <li>You agree not to use the service for any illegal activities.</li>
                  <li>We reserve the right to modify these terms at any time.</li>
                </ul>
                <p>By checking the box, you acknowledge that you have read and agree to these terms.</p>
              </div>
              <button 
                onClick={toggleTermsModal}
                className="close-modal"
              >
                I Understand
              </button>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default FormValidation;
