import React, { useState } from 'react';

const FormValidation = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    acceptTerms: false
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showTermsModal, setShowTermsModal] = useState(false);

  const validateForm = () => {
    const newErrors = {};

    // Username validation
    if (!formData.username.trim()) {
      newErrors.username = 'Username is required';
    } else if (formData.username.length < 3) {
      newErrors.username = 'Username must be at least 3 characters';
    }

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      newErrors.email = 'Please enter a valid email';
    }

    // Password validation
    const passwordError = validatePassword(formData.password);
    if (passwordError) {
      newErrors.password = passwordError;
    }

    // Confirm password validation
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ''
      });
    }
  };

  const toggleTermsModal = () => {
    setShowTermsModal(!showTermsModal);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      setIsSubmitting(true);
      // Simulate form submission
      console.log('Form submitted:', formData);
      setTimeout(() => {
        alert('Form submitted successfully!');
        setIsSubmitting(false);
      }, 1000);
    }
  };

  // Check password requirements and return error message if any
  const validatePassword = (password) => {
    if (!password) return 'Password is required';
    if (password.length < 8) return 'Password must be at least 8 characters';
    if (!/[a-z]/.test(password)) return 'Password must contain at least one lowercase letter';
    if (!/[A-Z]/.test(password)) return 'Password must contain at least one uppercase letter';
    if (!/\d/.test(password)) return 'Password must contain at least one number';
    if (!/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>/?]/.test(password)) {
      return 'Password must contain at least one special character';
    }
    return '';
  };

  // Get password error message if any
  const getPasswordError = () => {
    if (!formData.password) return 'Password is required';
    return validatePassword(formData.password);
  };

  // Only show error after user has started typing in the password field
  const showPasswordError = formData.password !== '';

  return (
    <div className="form-container">
      <h2>Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Username</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            className={errors.username ? 'error' : ''}
          />
          {errors.username && <span className="error-message">{errors.username}</span>}
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={errors.email ? 'error' : ''}
          />
          {errors.email && <span className="error-message">{errors.email}</span>}
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className={errors.password ? 'error' : ''}
            placeholder="Enter your password"
          />
          {showPasswordError && errors.password && (
            <span className="error-message">{errors.password}</span>
          )}
        </div>

        <div className="form-group">
          <label>Confirm Password</label>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            className={errors.confirmPassword ? 'error' : ''}
          />
          {errors.confirmPassword && (
            <span className="error-message">{errors.confirmPassword}</span>
          )}
        </div>

        <div className="terms-group">
          <div className="terms-card">
            <div className="terms-header">
              <svg className="terms-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M12,2C6.48,2 2,6.48 2,12C2,17.52 6.48,22 12,22C17.52,22 22,17.52 22,12C22,6.48 17.52,2 12,2M12,20C7.59,20 4,16.41 4,12C4,7.59 7.59,4 12,4C16.41,4 20,7.59 20,12C20,16.41 16.41,20 12,20M11,16.5L6.5,12L7.91,10.59L11,13.67L16.59,8.09L18,9.5L11,16.5Z" />
              </svg>
              <h3>Terms & Conditions</h3>
            </div>
            <div className="terms-content">
              <p>By creating an account, you agree to our</p>
              <div className="terms-actions">
                <label className="checkbox-container">
                  <input
                    type="checkbox"
                    name="acceptTerms"
                    checked={formData.acceptTerms}
                    onChange={handleChange}
                  />
                  <span className="checkmark"></span>
                  <span className="terms-text">I agree to the </span>
                  <button 
                    type="button" 
                    onClick={toggleTermsModal} 
                    className="terms-link"
                  >
                    Terms and Conditions
                  </button>
                </label>
                {errors.acceptTerms && (
                  <span className="error-message">
                    <svg className="error-icon" viewBox="0 0 24 24">
                      <path fill="currentColor" d="M11,15H13V17H11V15M11,7H13V13H11V7M12,2C6.47,2 2,6.5 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2M12,20A8,8 0 0,1 4,12A8,8 0 0,1 12,4A8,8 0 0,1 20,12A8,8 0 0,1 12,20Z" />
                    </svg>
                    {errors.acceptTerms}
                  </span>
                )}
              </div>
            </div>
          </div>
        </div>

        <button 
          type="submit" 
          disabled={isSubmitting || !formData.acceptTerms}
          className={!formData.acceptTerms ? 'disabled' : ''}
        >
          {isSubmitting ? 'Submitting...' : 'Submit'}
        </button>

        {showTermsModal && (
          <div className="modal-overlay" onClick={toggleTermsModal}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
              <div className="modal-header">
                <h3>Terms and Conditions</h3>
                <button onClick={toggleTermsModal} className="close-button">
                  <svg viewBox="0 0 24 24">
                    <path fill="currentColor" d="M19,6.41L17.59,5L12,10.59L6.41,5L5,6.41L10.59,12L5,17.59L6.41,19L12,13.41L17.59,19L19,17.59L13.41,12L19,6.41Z" />
                  </svg>
                </button>
              </div>
              <div className="modal-body">
                <div className="terms-scroll">
                  <div className="terms-section">
                    <h4>1. Acceptance of Terms</h4>
                    <p>By accessing or using our services, you agree to be bound by these Terms and Conditions.</p>
                  </div>

                  <div className="terms-section">
                    <h4>2. User Responsibilities</h4>
                    <ul>
                      <li>You must be at least 18 years old to use this service.</li>
                      <li>You are responsible for maintaining the confidentiality of your account.</li>
                      <li>You agree to provide accurate and complete information.</li>
                    </ul>
                  </div>

                  <div className="terms-section">
                    <h4>3. Prohibited Activities</h4>
                    <p>You agree not to:</p>
                    <ul>
                      <li>Use the service for any illegal activities</li>
                      <li>Attempt to gain unauthorized access to our systems</li>
                      <li>Interfere with other users' experience</li>
                    </ul>
                  </div>

                  <div className="terms-section">
                    <h4>4. Changes to Terms</h4>
                    <p>We reserve the right to modify these terms at any time. Your continued use of the service constitutes acceptance of any changes.</p>
                  </div>
                </div>

                <div className="terms-acceptance">
                  <p>By checking the box, you acknowledge that you have read, understood, and agree to be bound by these Terms and Conditions.</p>
                  <button 
                    onClick={toggleTermsModal}
                    className="close-modal"
                  >
                    <span>I Understand</span>
                    <svg className="check-icon" viewBox="0 0 24 24">
                      <path fill="currentColor" d="M21,7L9,19L3.5,13.5L4.91,12.09L9,16.17L19.59,5.59L21,7Z" />
                    </svg>
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default FormValidation;
